import './CalcForm.css';

import React, { Component, useState, useEffect, setState  } from 'react';

class CalcForm extends Component {

  constructor(props) {
    super(props);
    this.state = {}

    this.initDisplayStates();
    this.initRequestStates();
    this.initResponseStates();
  }

  initDisplayStates() {
    this.state.throuhputdefault=true;

    this.state.msgtimeunit=1;       // default = second
    this.state.msgsizeunit=1/1000;    // default = kilobytes

    this.state.nbmessages=0;
    this.state.messagesize=0;

    this.state.displayDisableThroughputBox=false;
    this.state.DisplayMessagesRateAreHidden=true;
  }

  initRequestStates() {
    this.state.req_inthroughput=0;
    this.state.req_replicas=3;
    this.state.req_netspeed=10;
    this.state.req_netpercent=80;
    this.state.req_diskspeed=400;
    this.state.req_diskpercent=80;
    this.state.req_consumers=5;
  }

  initReponseStates() {
    this.state.resp_inthroughput=0;
    this.state.resp_replicas=3;
    this.state.resp_netspeed=10;
    this.state.resp_netpercent=80;
    this.state.resp_diskspeed=400;
    this.state.resp_diskpercent=80;
    this.state.resp_consumers=5;
  }  

  componentDidMount() {

  }


  /** 
     render() is the main method of the component
     it is where the page html is displayed
     with the binding to the functions that will react to page events
  */
  render() {

    // some logic can be added here to compute dynamic value before the return method is called


    return (

        <div className="container">
          <h1 className="display-4">Red Hat AMQStreams sizing</h1>
            {/*<form onSubmit={this.callKafkaCalculator}>*/}

                {/*<div class="throughput">*/}
                <fieldset class="throughput">
                    <legend>Inbound throughput parameter</legend>

                    {/* Throughput as absolute or based on messages rate radio button box */}
                    <input type="radio" id="throughput-msg" name="throughput" value="msgsec" onClick={this.onThroughputSelectChange}/>
                    <label for="throughput-msg">message based</label><br/>
                    <input type="radio" id="throughput-absolute" name="throughput" value="thru" checked={this.state.throuhputdefault} onClick={this.onThroughputSelectChange}/>
                    <label for="throughput-absolute"> absolute value</label><br/>

                    {/* Throughput box */}
                    <div class="throughput-box1"> 
                        <div><label for="inboundrate">Throughput</label>
                             <input class="t-in3" type="text"  value={this.state.req_inthroughput} name="inboundrate" id="inrate" disabled={this.state.displayDisableThroughputBox}/> 
                             <label class="t-in">MB/sec</label> 
                        </div>
                    </div> 
                    <br/><br/>
                    {/* message rate box (when selected by radio button)*/}
                    <div class="throughput-box2" hidden={this.state.DisplayMessagesRateAreHidden}> 
                        <div><label for="nbmessages">Number of messages</label> 
                              <input class="t-in2" type="text" value={this.state.nbmessages} name="nbmessages" id="nbmessages" onInput={this.displayComputedThroughput} /> 
                              <select class="t-in" name="msg-time-unit" id="msg-time-unit" onChange={this.displayComputedThroughput}> 
                                <option value="sec">msg/sec</option> 
                                <option value="min">msg/min</option> 
                              </select> 
                        </div> 

                        <div><label for="messagesize">Average message size</label> 
                              <input class="t-in" type="text" value={this.state.messagesize} name="messagesize" id="messagesize" onInput={this.displayComputedThroughput} /> 
                              <select class="t-in" name="msg-size-unit" defaultValue="kbytes" id="msg-size-unit" onChange={this.displayComputedThroughput}> 
                              <option value="bytes">bytes</option> 
                              <option value="kbytes">KB</option> 
                            </select> 
                        </div> 
                    </div> 
                    <br/><br/><br/>                
                </fieldset>
                <br/>


                <label for="messageSize">Average Message Size (number of bytes):</label><br/>
                <input type="text" name="messageSize" id="messageSize"/><br/>

                <label for="messageSize" class="small">The average message size.</label><p></p><br/>    


                <label for="replicas">Number of Replicas:</label><br/>
                <input type="text" name="replicas" id="replicas" /><br/>

                <label for="replicas" class="small">Replicas are used for redundancy. It affects the amount of storage as well as 
                the performance of your Kafka cluster</label><p></p><br/>        

                <label for="netSpeed">Network Adapter Speed (Gbps):</label><br/>
                <input type="text" name="netSpeed" id="netSpeed" /><br/>

                <label for="netSpeed" class="small">The network adapter is assumed to be working in full-duplex mode. Standard Gigabit network adapter is 1 Gbps. 
                    It is likely that either the network adapter speed (or the max. disk throughput) is going to be the bottleneck of your system. 
                    You may want to use a higher speed network adapter if that is the case.</label><p></p><br/>        

                <label for="diskThroughput">Max. Disk Throughput (MB/s):</label><br/>
                <input type="text" name="diskThroughput" id="diskThroughput" /><br/>

                <label for="diskThroughput" class="small">For hard disks, it is around 125 MB/s. For SSDs, it could be 400MB/s or higher.</label><p></p><br/>        

                <label for="maxUtil">Max. Utilisation Allowed for Network Adapter and Disk Subsystem (between 0.01 and 1.00):</label><br/>
                <input type="text" name="maxUtil" id="maxUtil"/><br/>

                <label for="maxUtil" class="small">You do not want to have your network adapter and disk system running at 100% as this may impact the 
                    performance of the system during a sudden temporary surge in message rate.</label><p></p><br/>        

                <label for="consumerGroups">Number of Consumer Groups:</label><br/>
                <input type="text" name="consumerGroups" id="consumerGroups" /><br/>

                <label for="consumerGroups" class="small">A consumer group is a group of related consumers that perform a task, like sending messages to 
                    a service. Different consumer groups can read from different offsets in a partition.</label><p></p><br/>        

                <label for="laggingConsumers">Number of Lagging Consumers:</label><br/>
                <input type="text" name="laggingConsumers" id="laggingConsumers"/><br/>

                <label for="laggingConsumers" class="small">Caching of data reduces the need to read data from disk when readers (consumers in consumer groups) 
                    read data. However, the size of the cache is finite. When a slow reader tries to read the data, the data may have already been 
                    evicted from the cache necessitating a disk read which impacts performance. The more lagging consumers, the more impact on performance.
                </label>
                <ul>
                    <li>
                        <label for="laggingConsumers" class="small">0 is the best case scenario. 0 means that there are no lagging readers ie, 
                            all data are in the cache and the reads do not incur the penalty of reading from disk.
                        </label>
                    </li><br/>
                    <li>
                        <label for="laggingConsumers" class="small">num Consumer Groups + (num Replicas - 1) is the worst case scenario.
                            This implies that all reads from readers of the consumer groups as well as the followers trying to read the data from the leader 
                            to store them on their local disk incur the penalty of reading from disk.
                        </label>
                    </li>
                </ul>
                <p></p><br/>        

                <label for="retentionPariod">Data Retention Period (number of days):</label><br/>
                <input type="text" name="retentionPariod" id="retentionPariod"/><br/>

                <label for="retentionPariod" class="small">This parameter is used to estimate the total storage required for the solution.</label><p></p><br/>        


                <label for="zkFailures">Number of Zookeeper Node Failures that can be Tolerated (1 or 2):</label><br/>
                <input type="text" name="zkFailures" id="zkFailures"/><br/>

                <label for="zkFailures" class="small">A Zookeeper cluster keeps track of status of the Kafka cluster nodes and Kafka topics, 
                    partitions etc. The number of Zookeeper nodes is always an odd number: 3, 5, 7, etc. This parameter determines the size of your 
                    Zookeeper cluster.
                </label><p></p><hr/>

                <h2>Partition Parameters</h2>
                <p class="bolder">The following are optional parameters for estimating the minimum number of partitions required for a topic. This estimation will
                be skipped if any of the following fields are left at zero (0).
                </p>
                <p></p>

                <label for="topicThroughput">Target Throughput of a Topic (MB/s):</label><br/>
                <input type="text" name="topicThroughput" id="topicThroughput" /><br/>

                <label for="topicThroughput" class="small"> 
                    This parameter specifies the target throughput for a topic.  
                </label><p></p><br/>      

                <label for="producerThroughput">Throughput of the Slowest Producer (MB/s):</label><br/>
                <input type="text" name="producerThroughput" id="producerThroughput"/><br/>

                <label for="producerThroughput" class="small"> 
                    This parameter is used to estimate the minimum number of partitions needed for producers to produce the target message throughput.
                </label><p></p><br/>        

                <label for="consumerThroughput">Throughput of the Slowest Consumer (MB/s):</label><br/>
                <input type="text" name="consumerThroughput" id="consumerThroughput"/><br/>

                <label for="consumerThroughput" class="small">This parameter is used to estimate the minimum number of partitions needed for consumers to
                consume at the target message throughput.
                </label><p></p>
                <hr/>        

                <button type="submit" onClick={this.callKafkaCalculator}>Compute size</button>

        </div>
      );
  }


  // this function is linked to the radio buttons stored in the div of class throughput
  // in the memory (setState, which is initialized in the constructor), we store the name and the value
  // the name is the name of the radio button, and the value is the value of the radio button from the radio button definition
  // the event.target contains both.  it's also possible to use e.target.name ; e.target.value
  // the re-rendering is automated  
  onThroughputSelectChange = (e) => {

    const { name, value } = e.target;

    this.setState({
      [name]: value
    });

    // we need to adject the 'checked' value of th radio button for the next rendering
    if (e.target.value==="thru") {
        this.state.throuhputdefault=true;
        this.state.displayDisableThroughputBox=false;
        this.state.DisplayMessagesRateAreHidden=true;
    }
    else {
        this.state.throuhputdefault=false;
        this.state.displayDisableThroughputBox=true;
        this.state.DisplayMessagesRateAreHidden=false;
    }


  } ; 


  displayComputedThroughput = (e) => {    

    const { name, value } = e.target;
    this.setState({
      [name]: value
    });  

    switch (e.target.name) {
        case "nbmessages": 
            this.state.nbmessages = e.target.value;
            break;
        case "messagesize":
            this.state.messagesize = e.target.value;
            break;
        case "msg-time-unit": 
            if (e.target.value=="sec")  //TODO: hour, day
                this.state.msgtimeunit = 1;      
            if (e.target.value=="min")  //TODO: hour, day
                this.state.msgtimeunit = 60;
            break;
        case "msg-size-unit": 
            if (e.target.value=="bytes")  //TODO: ?
                this.state.msgsizeunit = 1/1000000;
            if (e.target.value=="kbytes")  //TODO: ?
                this.state.msgsizeunit = 1/1000;  
            break;
        default: break;
    };

    this.state.req_inthroughput = this.state.nbmessages * this.state.msgtimeunit * this.state.messagesize * this.state.msgsizeunit;
  };    


  callKafkaCalculator = () => {

    fetch('http://localhost:8090/api/kafkasizing', {
      method: 'POST',
      body: JSON.stringify({
        inbound_throughput: 100,
        replicas: 3,
        netspeed: 10,
        netpercent: 80,
        diskspeed: 400,
        diskpercent: 80,
        consumers: 5
      }),
      headers: {
        'Content-type': 'application/json; charset=UTF-8',
      },
      })
      .then((response) => response.json())
      .then((data) => {
          console.log(data);
          console.log(data.topicDesc.consumers);

        //setPosts((posts) => [data, ...posts]);
        //setTitle('');
        //setBody('');
      })
      .catch((err) => {
        console.log(err.message);
      });
  }    
} 

export default CalcForm;
