import './CalcForm.css';

import React, { Component, useState, useEffect, setState  } from 'react';

class CalcForm extends Component {

  constructor(props) {
    super(props);
    this.state = {}

    this.initDisplayStates();
    this.initRequestStates();
    this.initResponseStates();
    this.initComputingDisplayStates()
  }

  initDisplayStates() {
    this.state.throuhputdefault=true;
    this.state.displayDisableThroughputBox=false;
    this.state.DisplayMessagesRateAreHidden=true;    
    this.state.net10default=true;
    this.state.diskAutoDefault=true;
    this.state.DisplayAutoDisHidden=false;
    this.state.diskSSDDefault=true;
  }

  initRequestStates() {
    this.state.req_inthroughput=0;
    this.state.req_replicas=3;
    this.state.req_netspeed=10;
    this.state.req_netpercent=80;
    this.state.req_diskspeed=400;
    this.state.req_diskpercent=80;
    this.state.req_consumers=5;
  }

  initResponseStates() {
    this.state.resp_inthroughput=0;
    this.state.resp_replicas=3;
    this.state.resp_netspeed=10;
    this.state.resp_netpercent=80;
    this.state.resp_diskspeed=400;
    this.state.resp_diskpercent=80;
    this.state.resp_consumers=5;
  }  

  initComputingDisplayStates() {
    this.state.msgtimeunit=1;       // default = second
    this.state.msgsizeunit=1/1000;    // default = kilobytes

    this.state.nbmessages=0;
    this.state.messagesize=0;
  }

  componentDidMount() {
    document.getElementById("diskcustdiv").hidden=true;
    document.getElementById("disk-ssd").checked = true;
    document.getElementById("cgabsdiv").hidden = true;
    document.getElementById("lagvaldiv").hidden = true;
    document.getElementById("lag-pod").checked = true;

  }


  /** 
     render() is the main method of the component
     it is where the page html is displayed
     with the binding to the functions that will react to page events
  */
  render() {

    // some logic can be added here to compute dynamic value before the return method is called


    return (

        <div className="container">
          <h1 className="title">Red Hat AMQStreams sizing</h1>


                <fieldset class="throughput">
                    <legend>Inbound throughput</legend>

                    {/* Throughput as absolute or based on messages rate radio button box */}
                    <input type="radio" id="throughput-msg" name="throughput" value="msgsec" onClick={this.onThroughputSelectChange}/>
                    <label class="label-space" for="throughput-msg">message based</label><br/>
                    <input type="radio" id="throughput-absolute" name="throughput" value="thru" checked={this.state.throuhputdefault} onClick={this.onThroughputSelectChange}/>
                    <label class="label-space" for="throughput-absolute"> absolute value</label><br/>

                            {/* Throughput box */}
                    <div class="align-absthroughput"> 
                        <div><label class="label-space" for="inboundrate">Throughput</label>
                             <input class="space-throughput" type="text" size="8"  name="inboundrate" id="inrate" disabled={this.state.displayDisableThroughputBox}/> 
                             <label class="t-in">MB/sec</label> 
                        </div>
                    </div> 
                    <br/><br/>
                            {/* message rate box (when selected by radio button)*/} 
                    <div class="align-custthroughput" hidden={this.state.DisplayMessagesRateAreHidden}> 
                        <div><label class="label-space" for="nbmessages">Number of messages</label> 
                              <input class="space-nbmessages" type="text" size="8" value={this.state.nbmessages} name="nbmessages" id="nbmessages" onInput={this.displayComputedThroughput} /> 
                              <select class="space-units" name="msg-time-unit" id="msg-time-unit" onChange={this.displayComputedThroughput}> 
                                <option value="sec">msg/sec</option> 
                                <option value="min">msg/min</option> 
                              </select> 
                        </div> 

                        <div><label class="label-space" for="messagesize">Average message size</label> 
                              <input class="space-units" type="text" size="8" value={this.state.messagesize} name="messagesize" id="messagesize" onInput={this.displayComputedThroughput} /> 
                              <select class="space-units" name="msg-size-unit" defaultValue="kbytes" id="msg-size-unit" onChange={this.displayComputedThroughput}> 
                              <option value="bytes">bytes</option> 
                              <option value="kbytes">KB</option> 
                            </select> 
                        </div> 
                    </div> 
                    <br/><br/><br/>                
                </fieldset>
                <br/>


                <fieldset class="network">
                    <legend>Network Adapter Capabilities</legend>
                    <div class="net">
                            <input type="radio" id="net-speed-1" name="netspeed" value="1" onClick={this.onNetSelectChange}/>
                            <label class="label-space" for="net-speed-1">1 Gbps</label>
                            <input class="net-space" type="radio" id="net-peed-10" name="netspeed" value="10" defaultChecked onClick={this.onNetSelectChange}/>
                            <label class="label-space" for="net-speed-10">10 Gbps</label>
                    </div>
                    <label class="label-space" for="netsaturation">Network saturation</label> 
                    <input class="space-units" type="number"  min="1" max="100" size="4" defaultValue="80" name="netsaturation" id="netsaturation" /> 
                    <label class="label-space" for="netsaturation">%</label> 

                </fieldset>
                <br/>


                <fieldset class="disks">
                    <legend>I/O speed</legend>
                    <div class="disk">
                            <input class="space-units" type="radio" id="disk-choice1" name="diskchoice" value="auto" defaultChecked onClick={this.onDiskChoiceChange}/>
                            <label class="label-space" for="disk-choice1">auto</label>
                            <input class="space-units" class="net-space" type="radio" id="disk-choice2" name="diskchoice" value="custom" onClick={this.onDiskChoiceChange}/>
                            <label class="label-space" for="disk-choice2">custom</label>
                    </div>
                    <div class="align-disks" id="diskcustdiv">
                        <label class="label-space" for="disk-abs">Custom disk R/W throughput</label>
                        <input class="space-units" type="text" id="disk-abs" name="diskabs"/>
                        <label class="label-space" for="disk-abs">MB/s</label>

                    </div>                    
                    <div class="align-disks" id="diskautodiv">
                        <input type="radio" id="disk-ussd" name="disktype" value="ussd" onClick={this.onDiskTypeChange}/>
                        <label class="label-space" for="disk-ussd">Xtreme (1000MB/s)</label>
                        <input class="disk-space" type="radio" id="disk-ssd" name="disktype" value="ssd" onClick={this.onDiskTypeChange} />
                        <label class="label-space" for="disk-ssd">SSD (400MB/s)</label>
                        <input class="disk-space" type="radio" id="disk-uhigh" name="disktype" value="uhigh" onClick={this.onDiskTypeChange}/>
                        <label class="label-space" for="disk-uhigh">Ultra I/O (250MB/s)</label>   
                        <input class="disk-space" type="radio" id="disk-high" name="disktype" value="high" onClick={this.onDiskTypeChange}/>
                        <label class="label-space" for="disk-high">High I/O (150MB/s)</label>      
                        <input class="disk-space" type="radio" id="disk-std" name="disktype" value="std" onClick={this.onDiskTypeChange}/>
                        <label class="label-space" for="disk-std">Standard (50MB/s)</label>      
                    </div>
                    <br/>
                        <div id="diskpara">
                        <label class="label-space" for="nbdisks">Number of disk in parallel</label> 
                        <input class="space-units" type="number"  min="1" max="10" size="4" defaultValue="1" name="nbdisks" id="nbdisks" /> 
                        </div>
                        <br/>                 
                        <label class="label-space" for="disksaturation">I/O saturation</label> 
                        <input class="space-units" type="number" min="1" max="100" size="4" defaultValue="80" name="disksaturation" id="disksaturation" /> 
                        <label class="label-space" for="disksaturation">%</label> 

                    </fieldset>
                <br/>


                <fieldset class="consumers">
                    <legend>Messages delivery</legend>
                    <div class="consumers" >
                            <input class="cg-space" type="radio" id="abscg" name="cg" value="cgratio" onClick={this.onConsumersChoiceChange}/>
                            <label class="label-space" for="abscg">ratio</label>
                            <input class="cg-space" type="radio" id="custcg" defaultChecked name="cg" value="cgtopic" onClick={this.onConsumersChoiceChange}/>
                            <label class="label-space" for="custcg">based on applications</label>
                    </div>             
                    <div>
                        <div id="cgabsdiv" class="consumers" >   
                            <label class="label-space" for="cg">Average number of consuming applications per topic</label>
                            <input class="cg-space" type="number" name="cg" id="cgabs" min="1" max="30" size="4" defaultValue="1"/>
                        </div>
                        <div id="cgcustdiv" class="consumers">   
                            <label class="label-space" for="cgtopics">Total number of topics</label>
                            <input class="cg-space" type="text" name="cg" id="cgtopics" />
                            <label class="label-space" for="cgapps">Total number of consumer applications</label>
                            <input class="cg-space" type="text" name="cg" id="cgapps" />
                        </div>  
                    </div>                  
                    <br/>
                </fieldset>


                <fieldset class="lagging">
                    <legend>Lagging support</legend>
                    <div>
                            <input class="lag-space" type="radio" id="lagauto" name="lag" value="lagauto" defaultChecked onClick={this.onLagChoiceChange}/>
                            <label class="label-space" for="lagauo">auto</label>
                            <input class="lag-space" type="radio" id="lagval" name="lag" value="lagval" onClick={this.onLagChoiceChange}/>
                            <label class="label-space" for="lagval">fixed value</label>
                    </div>   
                    <div class="lagging-align" id="lagautodiv">
                        <input type="radio" id="lag-vm" name="lagtype" value="lagvm" onClick={this.onLagTypeChange}/>
                        <label class="label-space" for="lag-vm">VM (1 min)</label>
                        <input class="disk-space" type="radio" id="lag-pod" name="lagtype" value="lagpod" onClick={this.onlagTypeChange} />
                        <label class="label-space" for="lag-pod">Pod (30 sec)</label>
                        <input class="disk-space" type="radio" id="lag-func" name="lagtype" value="lagfunc" onClick={this.onLagTypeChange}/>
                        <label class="label-space" for="lag-func">Serverless (10 sec)</label>       
                    </div>                              
                    <div>
                        <div id="lagvaldiv" class="lagging">   
                            <label class="label-space" for="lagvalue">maximum allowed lagging time</label>
                            <input class="lag-space" type="number" name="lagvalue" id="lagvalue" min="O" max="300" defaultValue="0"/>
                            <label class="label-space" for="lagvalue">sec</label>
                        </div>  
                    </div>                  
                    <br/>
                </fieldset>



                <fieldset class="reliability">
                    <legend>reliability</legend>
                    <label class="label-space" for="replicas">Replication factor</label>
                    <input class="ha-space" type="number" name="replicas" id="replicas" min="1" max="5" defaultValue="3" /> 
                    <br/>    
                    <label class="label-space" for="zkha">Fault tolerance (maintenance mode)</label>
                    <input class="ha-space" type="number" name="akha" id="zkha" min="0" max="2" defaultValue="0" />   
                    <label class="label-space-plus" for="extranode">Throughput tolerance</label>
                    <input class="ha-space" type="number" name="extranode" id="extranode" min="0" max="5" defaultValue="1" /> 
                    <br/>
                    <label class="label-space" for="deviation">Peak Deviation</label>
                    <input class="ha-space" type="number" name="deviation" id="deviation" min="1" max="100" defaultValue="50" />  
                    <label class="label-space" for="deviation">%</label> 
                    <label class="label-space-plus-align" for="margin">Security margin</label>
                    <input class="ha-space" type="number" name="margin" id="margin" min="1" max="100" defaultValue="25" />  
                    <label class="label-space" for="margin">%</label>    
                    <br/><br/>
                    <input class="ch-space" type="checkbox" id="mm" name="mm" value="mm"/>
                    <label class="label-space" for="mm">Use of MirrorMaker 2</label>
                    <br/>                 
                </fieldset>




                <label for="retentionPariod">Data Retention Period (number of days):</label><br/>
                <input type="text" name="retentionPariod" id="retentionPariod"/><br/>

                <label for="retentionPariod" class="small">This parameter is used to estimate the total storage required for the solution.</label><p></p><br/>        


                <label for="zkFailures">Number of Zookeeper Node Failures that can be Tolerated (1 or 2):</label><br/>
                <input type="text" name="zkFailures" id="zkFailures"/><br/>

                <label for="zkFailures" class="small">A Zookeeper cluster keeps track of status of the Kafka cluster nodes and Kafka topics, 
                    partitions etc. The number of Zookeeper nodes is always an odd number: 3, 5, 7, etc. This parameter determines the size of your 
                    Zookeeper cluster.
                </label><p></p><hr/>

                <h2>Partition Parameters</h2>
                <p class="bolder">The following are optional parameters for estimating the minimum number of partitions required for a topic. This estimation will
                be skipped if any of the following fields are left at zero (0).
                </p>
                <p></p>

                <label for="topicThroughput">Target Throughput of a Topic (MB/s):</label><br/>
                <input type="text" name="topicThroughput" id="topicThroughput" /><br/>

                <label for="topicThroughput" class="small"> 
                    This parameter specifies the target throughput for a topic.  
                </label><p></p><br/>      

                <label for="producerThroughput">Throughput of the Slowest Producer (MB/s):</label><br/>
                <input type="text" name="producerThroughput" id="producerThroughput"/><br/>

                <label for="producerThroughput" class="small"> 
                    This parameter is used to estimate the minimum number of partitions needed for producers to produce the target message throughput.
                </label><p></p><br/>        

                <label for="consumerThroughput">Throughput of the Slowest Consumer (MB/s):</label><br/>
                <input type="text" name="consumerThroughput" id="consumerThroughput"/><br/>

                <label for="consumerThroughput" class="small">This parameter is used to estimate the minimum number of partitions needed for consumers to
                consume at the target message throughput.
                </label><p></p>
                <hr/>        

                <button type="submit" onClick={this.callKafkaCalculator}>Compute size</button>

        </div>
      );
  }


  // this function is linked to the radio buttons of the "throughput" section
  // we adjust the display according to the selected radio button
  // in the memory (setState, which is initialized in the constructor), we store the name and the value of the event
  // the name is the name of the radio button, and the value is the value of the radio button from the radio button definition
  // the event.target contains both.  it's also possible to use e.target.name ; e.target.value
  // the re-rendering is automated  
  onThroughputSelectChange = (e) => {

    const { name, value } = e.target;
    this.setState({
      [name]: value
    });

    // we need to adject the 'checked' value of th radio button for the next rendering
    if (e.target.value==="thru") {
        this.state.throuhputdefault=true;
        this.state.displayDisableThroughputBox=false;
        this.state.DisplayMessagesRateAreHidden=true;
    }
    if (e.target.value==="msgsec") {
        this.state.throuhputdefault=false;
        this.state.displayDisableThroughputBox=true;
        this.state.DisplayMessagesRateAreHidden=false;
        document.getElementById("inrate").value = this.state.req_inthroughput;
    }
  }

  displayComputedThroughput = (e) => {    

    const { name, value } = e.target;
    this.setState({
      [name]: value
    });  

    switch (e.target.name) {
        case "nbmessages": 
            this.state.nbmessages = e.target.value;
            break;
        case "messagesize":
            this.state.messagesize = e.target.value;
            break;
        case "msg-time-unit": 
            if (e.target.value=="sec")  //TODO: hour, day
                this.state.msgtimeunit = 1;      
            if (e.target.value=="min")  //TODO: hour, day
                this.state.msgtimeunit = 60;
            break;
        case "msg-size-unit": 
            if (e.target.value=="bytes")  //TODO: ?
                this.state.msgsizeunit = 1/1000000;
            if (e.target.value=="kbytes")  //TODO: ?
                this.state.msgsizeunit = 1/1000;  
            break;
        default: break;
    };

    this.state.req_inthroughput = this.state.nbmessages * this.state.msgtimeunit * this.state.messagesize * this.state.msgsizeunit;
    document.getElementById("inrate").value = this.state.req_inthroughput;
  };     


  onNetSelectChange = (e) => {
    const { name, value } = e.target;
    this.setState({
      [name]: value
    });
    if (e.target.value==="1") {
        this.state.net10default=false;
        this.state.req_netspeed=1;
    }
    if (e.target.value==="10") {
        this.state.net10default=true;
        this.state.req_netspeed=10;
    }    
  }

  onDiskChoiceChange = (e) => {

    const { name, value } = e.target;
    this.setState({
      [name]: value
    });
    if (e.target.value==="auto") {
        document.getElementById("diskautodiv").hidden=false;
        document.getElementById("diskcustdiv").hidden=true;
        document.getElementById("diskpara").hidden=false;
    }
    if (e.target.value==="custom") {
        document.getElementById("diskautodiv").hidden=true; 
        document.getElementById("diskcustdiv").hidden=false;
        document.getElementById("diskpara").hidden=true;
    }
}

  onDiskTypeChange = (e) => {

    switch (e.target.value) {
        case "ussd": document.getElementById("disk-ussd").checked = true; break;
        case "ssd": document.getElementById("disk-ssd").checked = true; break;
        case "uhigh": document.getElementById("disk-uhigh").checked = true; break;
        case "high": document.getElementById("disk-high").checked = true; break;
      }
  }

  onConsumersChoiceChange = (e) => {

    if (e.target.value==="cgratio") {
        document.getElementById("cgabsdiv").hidden = false;
        document.getElementById("cgcustdiv").hidden = true;
    }

    if (e.target.value==="cgtopic") {
        document.getElementById("cgabsdiv").hidden = true;
        document.getElementById("cgcustdiv").hidden = false;
    }
  }

  onLagChoiceChange = (e) => {

    if (e.target.value==="lagauto") {
        document.getElementById("lagvaldiv").hidden = true; 
        document.getElementById("lagautodiv").hidden = false;
    }
    if (e.target.value==="lagval") {
        document.getElementById("lagvaldiv").hidden = false; 
        document.getElementById("lagautodiv").hidden = true;
    }
  }

  onLagtypeChange = (e) => {

    switch (e.target.value) {
        case "lag-pod": document.getElementById("lag-pod").checked = true; 
        case "lag-vm": document.getElementById("lag-vm").checked = true; break;
        case "lag-func": document.getElementById("lag-func").checked = true; break;
    }

  }


  callKafkaCalculator = () => {

    fetch('http://localhost:8090/api/kafkasizing', {
      method: 'POST',
      body: JSON.stringify({
        inbound_throughput: this.state.req_inthroughput,
        replicas: this.state.req_replicas,
        netspeed: this.state.req_netspeed,
        netpercent: this.state.req_netpercent,
        diskspeed: this.state.req_diskspeed,
        diskpercent: this.state.req_diskpercent,
        consumers: this.state.req_consumers
      }),
      headers: {
        'Content-type': 'application/json; charset=UTF-8',
      },
      })
      .then((response) => response.json())
      .then((data) => {
          console.log(data);
          console.log(data.topicDesc.consumers);

        //setPosts((posts) => [data, ...posts]);
        //setTitle('');
        //setBody('');
      })
      .catch((err) => {
        console.log(err.message);
      });
  }    
} 

export default CalcForm;
